    def setDecimals( number, decimals ):
        number = str(number)
        numberAbs = number.split('.')[0]
        
        numberDecimals = ''
        if len(number.split(".")) > 1:
            numberDecimals = number.split('.')[1]
        while( len(numberDecimals) < decimals ):
            numberDecimals += "0"
        
        return numberAbs + numberDecimals
    
